import React, {useState, useEffect} from 'react'
import Header from './Header'
import axios from 'axios'


export default function Todo() {

    const [data, setData] = useState([]);

    const [input, setInput] = useState("");

    const handleClick =() =>{

        let object = {
            title: input
        }
        axios.post(`https://btm-rn.herokuapp.com/api/v1/todo`,  object )
        .then(res => {
            console.log(res);
            get()
          })
    }

    const get = async() =>{
        const result = await axios(
            'https://btm-rn.herokuapp.com/api/v1/todo',
          );

          setData(result.data.results);
    }

    useEffect(async () => {
        get()
      }, []);

      const UpdateTodo = (index) => {
        let inputText = input
        let DataArray = data

        if(inputText.length !== 0){
            DataArray[index].title = inputText
        setData([...DataArray])    
        }

      }

      const DeleteTodo = (index) => {
        let DataArray = data
        DataArray.splice(index, 1);
        setData([...DataArray])
      }

    return (

        <div>
            <Header />

            <input type="text" name="input" 
            onChange={(e)=> setInput(e.target.value)}
            style={{
            display: "flex",
            flex: 1,
            height: 20 ,
            alignItems: "stretch",       
            }}           
            />         
            <input  type="submit" value="Add" onClick={() =>handleClick()}/>  


            {
                data.map((val,i)=> <div><p key={i}>{val.title}</p> 

                <button style={{
                backgroundColor: "yellow",
                }}
                index={i}
                onClick={()=> UpdateTodo(i)}
                >   
                Update
                </button>

                <button style={{
                    backgroundColor: "Red",
                }}
                index={i}
                onClick={() => DeleteTodo(i)}
                >
                    Delete
                </button>
            </div>
             )}    





        </div>
    )
}
